import React, { useState } from 'react'
import "./auth_box.css"
import img1 from "../../assets/7.jpeg"
import img2 from "../../assets/8.jpeg"
import MediaQuery from 'react-responsive'
import { Link, Redirect } from 'react-router-dom'

function Auth_box(props) {
    const authType = props.props
    const [formData, setFormData] = useState({});
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };
    const submitAuthForm = () => {
        if (authType == "Login") {
            a = "http://localhost:8000/process_login"
        }
        else if (authType == "Sign up") {
            if (formData.doc_check == undefined || formData.doc_check == false) {
                a = "http://localhost:8000/register_patient"
            }
            else {
                return <Redirect to="/doctor_details" />

            }
        }
    }

    return (
        <div className='Auth_main'>
            <div className='SideImage'>
                <MediaQuery minWidth={1024}>
                    <img src={auth_type == "Login" ? (img1) : (img2)}>
                    </img>
                </MediaQuery>
            </div>
            <div className="AuthForm">
                <div className='formHeader'>
                    {auth_type}
                </div>
                <p>
                    Enter your details to {auth_type.toLowerCase()}
                </p>

                <form method='post' onSubmit={submitAuthForm} >
                    <input type='email' placeholder='Enter your email'
                        name='email'
                        value={formData.fieldName || ''}
                        onChange={handleInputChange}>
                    </input>
                    <input type='password' placeholder='Enter your password'
                        name='password'
                        value={formData.fieldName || ''}
                        onChange={handleInputChange} >
                    </input>
                    {
                        auth_type != "Login" ?
                            (<>
                                <input type='password' placeholder='Confirm your password'>
                                </input>
                                <label for="doc_check" >
                                    <input type="checkbox" id="doc_check" name='doc_check'
                                        value={formData.fieldName || ''}
                                        onChange={handleInputChange}
                                        style={{ position: "relative", top: 2, marginRight: 5 }}></input>
                                    Are you a doctor ?
                                </label>
                            </>
                            ) : <></>
                    }

                    <Link style={{ textDecoration: "none" }}>
                        <div className='trouble_link'>
                            Having trouble {auth_type == "Login" ? ("logging in") : ("signing up")}
                        </div>
                    </Link>
                    <button class="submit">{auth_type}</button>
                </form>
                <div className='acc_link' onClick={() => { }}>
                    {auth_type == "Login" ?
                        (<Link style={{ textDecoration: "none" }} to="/signup">
                            <div style={{ color: "#49535a" }} >
                                Don't have an account
                            </div>
                        </Link>)
                        :
                        (<Link style={{ textDecoration: "none" }} to="/login">
                            <div style={{ color: "#49535a" }} >
                                Already have an account
                            </div>
                        </Link>)}
                </div>
            </div>
        </div>
    )
}

export default Auth_box
